import React from "react";
import Link from "next/link";
import PostThumbnail from "./PostThumbnail";
import PostHeader from "./PostHeader";

class PostExcerpt extends React.Component {
  render() {
    const { index, post } = this.props;
    return (
      <article className="" key={index}>
        <Link href={"/posts/" + post.slug}>
          <a className="post-thumbnail">
            <PostThumbnail
              thumbnailImage={post.thumbnailImage}
              altText={post.title}
            />
          </a>
        </Link>
        <Link href={"/posts/" + post.slug}>
          <a className="title">
            <h2 className="">{post.title}</h2>
          </a>
        </Link>
        <PostHeader tags={post.tags} date={post.date} />
        <p className="excerpt">{post.excerpt}</p>
        <Link href={"/posts/" + post.slug}>
          <a className="keep-reading">Seguir Leyendo</a>
        </Link>
        <style jsx>
          {`
            article {
              font-family: "Open Sans", sans-serif;
              color: #333333;
              font-size: 1rem;
              margin: 1rem 1rem;
              border-bottom: 1px solid #eef2f8;
            }
            article a {
              text-decoration: none;
              color: #333333;
            }
            article > .title {
              margin: 1rem 0;
            }
            article > .keep-reading {
              cursor: pointer;
              color: #349ef3;
              margin: 1rem 0 0.5rem 0;
              display: block;
            }
            article > .excerpt {
              font-size: 1rem;
              line-height: 30px;
              text-align: justify;
              text-rendering: optimizelegibility;
              letter-spacing: -0.3px;
              font-family: Merriweather, serif;
              color: #5d686f;
              font-weight: 400;
            }
            @media screen and (max-width: 728px) {
              .post-thumbnail {
                display: none;
              }
            }
          `}
        </style>
      </article>
    );
  }
}

export default PostExcerpt;
