import Tag from "./Tag";

class PostHeader extends React.Component {

    render() {
        const {date, tags} = this.props;
            return (
                <small className="">
                <strong>Fecha publicaci√≥n: </strong>
                {date}
                <strong> Autor: </strong> Sebastian Gomez
                <strong> En: </strong>
                {tags.map((tag, index) => <Tag name={tag} key={index} />)}
                </small>
            );
    }
}


export default PostHeader;
