import React from "react";

class PostThumbnail extends React.Component {

    render() {
        const {thumbnailImage, altText} = this.props
        if (!thumbnailImage) {
            return (
              <img
                src={thumbnailImage || "/default-image.png"}
                width="150px"
                className="img-fluid"
                alt={altText}
              />
            );
        }
    }
}


export default PostThumbnail;
