import React from "react";
import Profile from "../components/Profile";

class Nav extends React.Component {
  constructor(props) {
    super(props);
    this.toggleMenu = this.toggleMenu.bind(this);
    this.menuOpened = false;
  }
  toggleMenu() {
    const links = document.querySelector(".links");
    const content = document.querySelector(".content");
    const topBar = document.querySelector(".top-bar");
    if (!this.menuOpened) {
      links.style["display"] = "flex";
      setTimeout(() => {
        links.style["width"] = "250px";
        content.style["transform"] = "translate3d(250px, 0, 0)";
        topBar.style["transform"] = "translate3d(250px, 0, 0)";
      }, 100);
      this.menuOpened = true;
    } else {
      links.style["width"] = "0";
      content.style["transform"] = "translate3d(0, 0, 0)";
      topBar.style["transform"] = "translate3d(0, 0, 0)";
      setTimeout(() => {
        links.style["display"] = "none";
      }, 100);
      this.menuOpened = false;
    }
  }
  render() {
    return (
      <nav>
        <div className="top-bar">
          <i onClick={this.toggleMenu} className="icon ion-md-menu"></i>
          <h1>Sebastian Gomez</h1>
          <img src="foto_personal.jpg" width="32px" height="32px" />
        </div>
        <ul className="links">
          <div className="menu-header">
            <img src="foto_personal.jpg" />
            <h2>Sebastian Gomez</h2>
          </div>

          <Profile />
          <li>
            <a>
              <i className="icon ion-md-home"></i> <span>Inicio</span>
            </a>
          </li>
          <li>
            <a>
              <i className="icon ion-md-home"></i> <span>Inicio</span>
            </a>
          </li>
          <li>
            <a>
              <i className="icon ion-md-bookmark"></i> <span>Categorías</span>
            </a>
          </li>
          <li>
            <a>
              <i className="icon ion-md-clipboard"></i> <span>Docencia</span>
            </a>
          </li>
          <li>
            <a>
              <i className="icon ion-md-contact"></i> <span>Sobre Mí</span>
            </a>
          </li>
          <li>
            <a>
              <i className="icon ion-md-search"></i> <span>Buscar</span>
            </a>
          </li>
          <li>
            <a>
              <i className="icon ion-md-mic"></i> <span>Charlas</span>
            </a>
          </li>
          <li>
            <a>
              <i className="icon ion-logo-rss"></i> <span>Rss</span>
            </a>
          </li>
          <li>
            <a>
              <i className="icon ion-logo-twitter"></i> <span>Twitter</span>
            </a>
          </li>
          <li>
            <a>
              <i className="icon ion-logo-facebook"></i> <span>Facebook</span>
            </a>
          </li>
          <li>
            <a>
              <i className="icon ion-logo-linkedin"></i> <span>LinkedIn</span>
            </a>
          </li>
        </ul>
        <style jsx>{`
          .top-bar {
            width: 100%;
            height: 3em;
            top: 0;
            position: fixed;
            display: flex;
            align-items: center;
            justify-content: space-between;
            border-bottom: 1px solid #eef2f8;
            color: #88909a;
            background-color: white;
            z-index: 1;
            transition: 0.3s; /* 0.5 second transition effect to slide in the sidenav */
          }
          .links {
            height: 100%; /* 100% Full-height */
            width: 0; /* 0 width - change this with JavaScript */
            position: fixed; /* Stay in place */
            z-index: 1; /* Stay on top */
            top: 0; /* Stay at the top */
            left: 0;
            background-image: linear-gradient(
              141deg,
              #9fb8ad 0%,
              #1fc8db 51%,
              #2cb5e8 75%
            ); /* Black*/
            overflow-x: hidden; /* Disable horizontal scroll */
            transition: 0.3s; /* 0.5 second transition effect to slide in the sidenav */
            justify-content: space-between;
            flex-wrap: wrap;
            flex-direction: column;
            color: #eeeeee;
            display: flex;
          }
          .links a {
            padding: 8px 8px 8px 32px;
            text-decoration: none;
            font-size: 25px;
            color: #818181;
            display: block;
            transition: 0.3s;
          }
          .links h2 {
            font-size: 1.2em;
          }
          .links .menu-header {
            text-align: center;
          }
          .links img {
            max-width: 100px;
          }
          .links li {
            list-style: none;
            padding-left: 1em;
          }
          img {
            border-radius: 50%;
            margin-right: 1em;
          }
          .links > li > a {
            color: white;
            font-size: 1rem;
          }
          .top-bar > .icon {
            font-size: 1.5em;
            color: #5d686f;
            padding-left: 1em;
          }
          @media screen and (min-width: 767px) {
            .top-bar {
              display: none;
            }
            .links {
              display: flex;
            }
          }
          @media screen and (max-width: 767px) {
            nav {
              margin-top: 4rem;
            }
            .top-bar {
              display: flex;
            }
            .links {
              display: none;
            }
          }
          @media screen and (min-width: 768px) and (max-width: 1024px) {
            nav {
              margin-top: 4rem;
            }
            .links {
              display: flex;
              width: 75px;
            }
            img {
              width: 40px;
              margin: 1em 1em;
            }
            h2 {
              display: none;
            }
            span {
              display: none;
            }
            .links > li > a {
              text-align: center;
              color: white;
              padding: 0 0 1rem 0;
              font-size: 1.5rem;
            }
            .links li {
              padding-left: 0;
              padding-bottom: 1rem;
            }
          }
          @media screen and (min-width: 1025px) and (max-width: 1280px) {
            .links {
              display: flex;
              width: 250px;
              padding-top: 1rem;
            }
            .links h2 {
              font-size: 1.2em;
              margin: 1rem 0;
            }
            .links li {
              padding-left: 0;
              padding-bottom: 1rem;
            }
          }
          @media screen and (min-width: 1281px) {
            .links {
              display: flex;
              width: 500px;
              padding-top: 1rem;
            }
            .links li a {
              font-size: 1.2rem;
            }
          }
        `}</style>
      </nav>
    );
  }
}

export default Nav;
