export default function Layout(props) {
  return (
    <article className="post">
      {props.children}
      <style jsx>{`
        .post {
          max-width: 1024px;
          margin: 0 auto;
          text-align: justify;
          text-rendering: optimizelegibility;
          letter-spacing: -0.3px;
          font-family: "Merriweather", serif;
          color: #5d686f;
          font-weight: 400;
          font-size: 17px;
        }
        .post > p {
          margin-bottom: 1rem;
        }
      `}</style>
    </article>
  );
}
