import React from "react";
import Link from "next/link"


class PostExcerpt extends React.Component {

    getImage(post) {
        if (!post.thumbnailImage) {
          return (
            <img
              src="/default-image.png"
              width="150px"
              className="img-fluid"
              alt={post.title}
            />
          );
        }
        return (
          <img
            src={post.thumbnailImage}
            width="150px"
            className="img-fluid"
            alt={post.title}
          />
        );
      }
      buildTag(tag, index) {
        const tagColors = [
          "primary",
          "secondary",
          "success",
          "danger",
          "warning",
          "info",
          "dark"
        ];
        const tagColor =
          "badge badge-pill badge-" +
          tagColors[Math.floor(Math.random() * tagColors.length)];
        return (
          <span key={index} className={tagColor}>
            {tag}
          </span>
        );
      }
    render(){
        const {index, post} = this.props;
        return <article className="card border-0 m-2 article" key={index}>
        <div className="row no-gutters">
          <div className="col-auto">{this.getImage(post)}</div>
          <div className="col">
            <div className="card-block px-2">
              <h2 className="card-title">{post.title}</h2>

              <small className="text-muted">
                <strong>Fecha publicaci√≥n: </strong>
                {post.date}
                <strong> Autor: </strong> Sebastian Gomez
                <strong> En: </strong>
                {post.tags.map((tag, index) => {
                  return this.buildTag(tag, index);
                })}
              </small>
              <p className="card-text text-justify">
                {post.excerpt}
              </p>
              <Link href="/[slug]" as={post.slug}>
                <a>Seguir Leyendo</a>
              </Link>
            </div>
            <hr />
          </div>
        </div>
      </article>
    }
}

export default PostExcerpt;
