import React from "react";


class PostExcerpt extends React.Component {

    getImage(post) {
        if (!post.thumbnailImage) {
          return (
            <img
              src="/default-image.png"
              width="150px"
              className="img-fluid"
              alt={post.title}
            />
          );
        }
        return (
          <img
            src={post.thumbnailImage}
            width="150px"
            className="img-fluid"
            alt={post.title}
          />
        );
      }
    render(){
        const {index, post} = this.props;
        return <div className="card border-0 m-2 article" key={index}>
        <div className="row no-gutters">
          <div className="col-auto">{this.getImage(post)}</div>
          <div className="col">
            <div className="card-block px-2">
              <h2 className="card-title">{post.title}</h2>

              <small className="text-muted">
                <strong>Fecha publicaci√≥n: </strong>
                <Moment
                  date={post.date}
                  format="D MMM YYYY"
                  withTitle
                />
                <strong> Autor: </strong> Sebastian Gomez
                <strong> En: </strong>
                {post.tags.map((tag, index) => {
                  return this.buildTag(tag, index);
                })}
              </small>
              <p className="card-text text-justify">
                {post.excerpt}
              </p>
              <Link href="/[slug]" as={post.slug}>
                <a>Seguir Leyendo</a>
              </Link>
            </div>
            <hr />
          </div>
        </div>
      </div>
    }
}

export default PostExcerpt;
