import React from "react";
import Link from "next/link";

const TagsColorDictionary = {
    'CSS':"primary",
    'HTML': "secondary",
    'Javascript': "warning",
    'Angular': "danger",
    'React': "success",
    'Desarrollo Web': "info",
    'Other': "dark"
}


class Tag extends React.Component {

    render() {
        const {name} = this.props
        console.log(name)
        const tagColor = TagsColorDictionary[name] || "dark";
        return (
            <Link href={"/tags/"+name}>
                <a className={"badge badge-pill badge-"+tagColor}>
                    {name}
                </a>
            </Link>            
        )
    }

}

export default Tag;
