import React from "react";

class Discuss extends React.Component {
  constructor(props) {
    super(props);
    this.canonicalUrl = false;
    this.state = {
      canonicalUrl: false,
    };
  }
  componentDidMount() {
    this.setState({ canonicalUrl: window.location.href });
  }
  render() {
    return (
      <React.Fragment>
        <div id="disqus_thread"></div>
        {this.state.canonicalUrl ? (
          <script
            dangerouslySetInnerHTML={{
              __html: `
          var disqus_config = function () {
            this.page.url = '${this.state.canonicalUrl}';
            this.page.identifier = "${this.props.slug}"; // Replace PAGE_IDENTIFIER with your page's unique identifier variable
          };

          (function() { // DON'T EDIT BELOW THIS LINE
          var d = document, s = d.createElement('script');
          s.src = 'https://EXAMPLE.disqus.com/embed.js';
          s.setAttribute('data-timestamp', +new Date());
          (d.head || d.body).appendChild(s);
          })();`,
            }}
          ></script>
        ) : (
          <p>Loading dicuss</p>
        )}
      </React.Fragment>
    );
  }
}

export default Discuss;
