import React from "react";

const posts = [
    {id: 1, title: 'todo sobre animaciones en css', slug: 'todo-sobre-animaciones-en-css', excerpt: `Las transiciones hacen parte del conjunto de herramientas que poseemos como desarrolladores FrontEnd para mejorar la experiencia del usuario dentro de nuestra aplicación Web. Son útiles porque nos permiten animar el cambio de valores en las distintas propiedades de un elemento lo que puede hacerlo más llamativo al usuario e invitarlo a interactuar con él. En este post trataré de cubrir el extenso tema de transiciones con diversos ejemplos adaptados desde la especificación.`}
]

class PostList extends React.Component {

    render() {
        const {currentPage = 0, totalPages} = this.props;
        return <div className="row">
                    <div className="col-sm">
                    <Link href={"/page/"+ parseInt(currentPage+1)} >
                        <a className="btn btn-light">
                        Artículos Anteriores
                        </a>
                    </Link>
                    </div>
                    <div className="col-sm text-right">
                    página 1 de {totalPages}
                    </div>
                </div>
    }
}

export default PostList;
