import React from "react";
import Link from "next/link"
import PostThumbnail from "./PostThumbnail";
import PostHeader from "./PostHeader";

class PostExcerpt extends React.Component {

    render(){
        const {index, post} = this.props;
        return (
        <article className="" key={index}>
        <div className="">
          <Link href={"/posts/"+post.slug} className="" >
              <a>
                <PostThumbnail thumbnailImage={post.thumbnailImage} altText={post.title} />
              </a>
          </Link>
          <div className="">
            <div className="">
            <Link href={"/posts/"+post.slug}>
                <a>
                    <h2 className="">{post.title}</h2>
                </a>
            </Link>
              <PostHeader tags={post.tags} date={post.date} />
              <p className="">
                {post.excerpt}
              </p>
              <Link href={"/posts/" + post.slug}>
                <a>Seguir Leyendo</a>
              </Link>
            </div>
            <hr />
          </div>
        </div>
      </article>
        )
    }
}

export default PostExcerpt;
