import React from "react";
import PostNav from "./PostNav";

class Layout extends React.Component {
  setDiscussScript = () => {
    return {
      __html: `
          var disqus_config = function () {
            this.page.url = "https://www.sebastian-gomez.com";  // Replace PAGE_URL with your page's canonical URL variable
            this.page.identifier = "test"; // Replace PAGE_IDENTIFIER with your page's unique identifier variable
          };

          (function() { // DON'T EDIT BELOW THIS LINE
          var d = document, s = d.createElement('script');
          s.src = 'https://EXAMPLE.disqus.com/embed.js';
          s.setAttribute('data-timestamp', +new Date());
          (d.head || d.body).appendChild(s);
          })();`,
    };
  };
  render() {
    const props = this.props;
    console.log(props.children);
    return (
      <React.Fragment>
        <PostNav />
        <article className="post">
          {props.children}
          <style jsx>{`
            .post {
              max-width: 1024px;
              margin: 4rem auto 1rem auto;
              text-align: justify;
              text-rendering: optimizelegibility;
              letter-spacing: -0.3px;
              font-family: "Merriweather", serif;
              color: #5d686f;
              font-weight: 400;
              font-size: 20px;
              line-height: 30px;
              padding: 1rem 1rem;
              transition: 0.3s all;
            }
          `}</style>
        </article>
        <discuss>
          <div id="disqus_thread"></div>
          <script dangerouslySetInnerHTML={this.setDiscussScript()}></script>
        </discuss>
        <footer></footer>
      </React.Fragment>
    );
  }
}

export default Layout;
