import React from "react";
import Link from "next/link"
import Tag from "./Tag";
import PostThumbnail from "./PostThumbnail";
import PostHeader from "./PostHeader";

class PostExcerpt extends React.Component {

    render(){
        const {index, post} = this.props;
        return (
        <article className="card border-0 m-2 article" key={index}>
        <div className="row no-gutters">
          <Link href={"/posts/"+post.slug} className="col-auto" >
              <a>
                <PostThumbnail thumbnailImage={post.thumbnailImage} altText={post.title} />
              </a>
          </Link>
          <div className="col">
            <div className="card-block px-2">
            <Link href={"/posts/"+post.slug}>
                <a>
                    <h2 className="card-title">{post.title}</h2>
                </a>
            </Link>
              <PostHeader tags={post.tags} date={post.date} />
              <p className="card-text text-justify">
                {post.excerpt}
              </p>
              <Link href={"/posts/" + post.slug}>
                <a>Seguir Leyendo</a>
              </Link>
            </div>
            <hr />
          </div>
        </div>
      </article>
        )
    }
}

export default PostExcerpt;
