import React from "react";
import PostNav from "./PostNav";

class Layout extends React.Component {
  render(props) {
    return (
      <React.Fragment>
        <PostNav />
        <article className="post">
          {props.children}
          <style jsx>{`
            .post {
              max-width: 1024px;
              margin: 1rem auto;
              text-align: justify;
              text-rendering: optimizelegibility;
              letter-spacing: -0.3px;
              font-family: "Merriweather", serif;
              color: #5d686f;
              font-weight: 400;
              font-size: 17px;
              line-height: 30px;
              padding: 1rem 1rem;
            }
          `}</style>
        </article>
      </React.Fragment>
    );
  }
}

export default Layout;
